import React, { useState, useEffect } from 'react';
import SockJsClient from 'react-stomp';
import './App.css';
import Input from './components/Input/Input';
import LoginForm from './components/LoginForm';
import Messages from './components/Messages/Messages';
import chatAPI from './services/chatapi';
import { randomColor } from './utils/common';
import ConnectedUsers from './components/ConnectedUsers/ConnectedUsers';

const SOCKET_URL = 'http://localhost:8080/ws-chat';

const App = () => {
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState(null);
  const [client, setClient] = useState(null);

  useEffect(() => {
    chatAPI.getMessages()
      .then(response => {
        setMessages(response.data);
      })
      .catch(err => {
        console.log('Error fetching messages:', err);
      });
  }, []);

  const onConnected = () => {
    console.log("Connected!!");
    if (client) {
      client.sendMessage('/app/registerUser', JSON.stringify({ username: user.username }));
    }
  };

  const onMessageReceived = (msg) => {
    console.log('New Message Received!!', msg);
    setMessages(prevMessages => [...prevMessages, msg]);
  };

  const onSendMessage = (msgText, file) => {
    const formData = new FormData();
    formData.append('sender', user.username);
    formData.append('content', msgText);

    if (file) {
      formData.append('file', file);
    }

    chatAPI.sendMessage(formData)
      .then(res => {
        console.log('Sent', res);
      })
      .catch(err => {
        console.log('Error occurred while sending message to API', err);
      });
  };

  const handleLoginSubmit = (username) => {
    console.log(username, " Logged in..");
    setUser({
      username: username,
      color: randomColor()
    });
  };

  return (
    <div className="App">
      {!!user ?
        (
          <>
            <div className="user-info">
              <h2>Logged in as: {user.username}</h2>
            </div>
            <div className="App">
              <h1>Welcome to the Chat App</h1>
              <ConnectedUsers />
              <SockJsClient
                url={SOCKET_URL}
                topics={['/topic/users']}
                onConnect={() => onConnected()}
                onMessage={msg => onMessageReceived(msg)}
                ref={clientRef => setClient(clientRef)}
                debug={false}
              />
              <Messages
                messages={messages}
                currentUser={user}
              />
              <Input onSendMessage={onSendMessage} />
            </div>
          </>
        ) :
        <LoginForm onSubmit={handleLoginSubmit} />
      }
    </div>
  );
}

export default App;

